import React, {useState, useEffect, Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import { Button, NavDropdown, Nav, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import Board from './board.js'
import Scrollspy from 'react-scrollspy'

const squareStyles = {
    display:'flex',
    width:'100%',
    height:'100%',
}
const BOARD_SIZE = 600;
const BOARD_PAD = 20;
function draw(){
    var c = document.getElementById("ChessBoard");
    var ctx = c.getContext("2d");
    var startX = 0, startY = 0;
    var blockSide = (BOARD_SIZE - 2 * BOARD_PAD) / 8;
    var count = 0;


    for (var i = 0; i < 8; i += 1){
        for (var j = 0; j < 8; j += 1){
            startX = blockSide * i + BOARD_PAD;
            startY = blockSide * j + BOARD_PAD;
            if (count % 2 == 0){
                ctx.fillStyle = (i % 2 == 0) ? "#edd0a7" : "#c86b12";
            } else {
                ctx.fillStyle = (i % 2 == 0) ? "#c86b12" : "#edd0a7";
            }
            ctx.fillRect(startX, startY, blockSide, blockSide);
            count = (count % 2 == 0) ? 1 : 0;

        }
    }

    for (var i = 0; i <= 8; i++){
        for (var j = 0; j <= 8; j++){
            ctx.moveTo(BOARD_PAD, blockSide * j + BOARD_PAD); 
            ctx.lineTo(BOARD_SIZE - BOARD_PAD, blockSide * j + BOARD_PAD);
            ctx.stroke(); // Draws horizontal bars

            ctx.moveTo(blockSide * i + BOARD_PAD, BOARD_PAD);
            ctx.lineTo(blockSide * i + BOARD_PAD, BOARD_SIZE - BOARD_PAD);
            ctx.stroke(); // Draws vertical bars

            }
        }
}
function Sample() {

  return (
    <Container>
        <Jumbotron id="home">
            <canvas id='ChessBoard' width={BOARD_SIZE} height={BOARD_SIZE} padding={20} onClick={e => draw()}>Your browser does not support the graphics.</canvas>
        </Jumbotron>

    </Container>


  );
}

export default Sample;
