import React, {useState, useEffect, Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import { Button, NavDropdown, Nav, Card, CardDeck, Modal } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import Board from './board.js'
import Scrollspy from 'react-scrollspy'

const squareStyles = {
    display:'flex',
    width:'100%',
    height:'100%',
}
const BOARD_SIZE = 600;
const BOARD_PAD = 20;
function draw(){
    var c = document.getElementById("ChessBoard");
    var ctx = c.getContext("2d");
    var startX = 0, startY = 0;
    var blockSide = (BOARD_SIZE - 2 * BOARD_PAD) / 8;
    var count = 0;


    for (var i = 0; i < 8; i += 1){
        for (var j = 0; j < 8; j += 1){
            startX = blockSide * i + BOARD_PAD;
            startY = blockSide * j + BOARD_PAD;
            if (count % 2 == 0){
                ctx.fillStyle = (i % 2 == 0) ? "#edd0a7" : "#c86b12";
            } else {
                ctx.fillStyle = (i % 2 == 0) ? "#c86b12" : "#edd0a7";
            }
            ctx.fillRect(startX, startY, blockSide, blockSide);
            count = (count % 2 == 0) ? 1 : 0;

        }
    }

    for (var i = 0; i <= 8; i++){
        for (var j = 0; j <= 8; j++){
            ctx.moveTo(BOARD_PAD, blockSide * j + BOARD_PAD); 
            ctx.lineTo(BOARD_SIZE - BOARD_PAD, blockSide * j + BOARD_PAD);
            ctx.stroke(); // Draws horizontal bars

            ctx.moveTo(blockSide * i + BOARD_PAD, BOARD_PAD);
            ctx.lineTo(blockSide * i + BOARD_PAD, BOARD_SIZE - BOARD_PAD);
            ctx.stroke(); // Draws vertical bars

            }
        }
}
function Sample() {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    function startTask(){
        draw();
        handleShow();
    }
  return (
    <Container>
        <Jumbotron id="home" style={{textAlign: 'center'}}>
            <canvas id='ChessBoard' width={BOARD_SIZE} height={BOARD_SIZE} padding={20} onLoad={e => draw()}>Your browser does not support the graphics.</canvas>
        </Jumbotron>
        <CardDeck>
            <Card>
                <Card.Img variant="top" src="holder.js/100px160" />
                <Card.Body>
                    <Card.Title>Chess Board Game</Card.Title>
                    <Card.Text>
                        This is an implementation of chess using Python for all the logic to illustrate how far simple ideas can go.
                    </Card.Text>
                    </Card.Body>
                <Card.Footer>
                    <Button variant='primary' onClick={startTask}>Play Game</Button>
                </Card.Footer>
            </Card>
            <Card>
                <Card.Img variant="top" src="holder.js/100px160" />
                <Card.Body>
                <Card.Title>Card title</Card.Title>
                <Card.Text>
                    This card has supporting text below as a natural lead-in to additional
                    content.{' '}
                </Card.Text>
                </Card.Body>
                <Card.Footer>
                <small className="text-muted">Last updated 3 mins ago</small>
                </Card.Footer>
            </Card>
            <Card>
                <Card.Img variant="top" src="holder.js/100px160" />
                <Card.Body>
                <Card.Title>Card title</Card.Title>
                <Card.Text>
                    This is a wider card with supporting text below as a natural lead-in to
                    additional content. This card has even longer content than the first to
                    show that equal height action.
                </Card.Text>
                </Card.Body>
                <Card.Footer>
                <small className="text-muted">Last updated 3 mins ago</small>
                </Card.Footer>
            </Card>
        </CardDeck>
        <Modal show={show} onHide={handleClose}>
        <Modal.Header closeButton>
          <Modal.Title>Modal heading</Modal.Title>
        </Modal.Header>
        <Modal.Body>
            Woohoo, you're reading this text in a modal!
            </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal> 
    </Container>


  );
}

export default Sample;
