import React, {useState} from 'react';
import './App.css';
import logo from './github.png';
import white_pawn from './sprites/white_pawn.svg'
import board_im from './board.png'
import Jumbotron from 'react-bootstrap/Jumbotron';
import Container from 'react-bootstrap/Container';
import { Button, Card, CardDeck, Modal, OverlayTrigger, Tooltip} from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import $ from 'jquery';
import './board.png'
const squareStyles = {
    display:'flex',
    width:'100%',
    height:'100%',
}
const BOARD_PAD = 20;

function draw(){
    console.log('We are actually running this dumb method');
    var c = document.createElement("canvas");
    c.id="chessboard"
    var temp = document.getElementById("game_loc")
    console.log()
    document.getElementById("test").style.maxWidth="60%";
    c.style.width="60%";
    const BOARD_SIZE = Math.round(c.width);
    c.width = c.height = BOARD_SIZE;
    temp.appendChild(c);
    var ctx = c.getContext("2d");
    ctx.lineWidth =1;
    var startX = 0, startY = 0;
    var blockSide = (BOARD_SIZE - 2 * BOARD_PAD) / 8;
    var count = 0;
    ctx.translate(c.width - Math.round(c.width), c.width - Math.round(c.width))
    for (var i = 0; i < 8; i += 1){
        for (var j = 0; j < 8; j += 1){
            startX = blockSide * i + BOARD_PAD;
            startY = blockSide * j + BOARD_PAD;
            if (count % 2 === 0){
                ctx.fillStyle = (i % 2 === 0) ? "#edd0a7" : "#B58962";
            } else {
                ctx.fillStyle = (i % 2 === 0) ? "#B58962" : "#edd0a7";
            }
            ctx.fillRect(startX, startY, blockSide, blockSide);
            count = (count % 2 === 0) ? 1 : 0;

        }
    }

    for (i = 0; i <= 8; i++){
        for (j = 0; j <= 8; j++){
            ctx.beginPath()
            ctx.moveTo(BOARD_PAD, blockSide * j + BOARD_PAD); 
            ctx.lineTo(BOARD_SIZE - BOARD_PAD, blockSide * j + BOARD_PAD);
            ctx.stroke(); // Draws horizontal bars

            ctx.beginPath()
            ctx.moveTo(blockSide * i + BOARD_PAD, BOARD_PAD);
            ctx.lineTo(blockSide * i + BOARD_PAD, BOARD_SIZE - BOARD_PAD);
            ctx.stroke(); // Draws vertical bars

            }
        }

    var pawn = new Image(blockSide, blockSide);
    pawn.src = './black_pawn.svg';
    pawn = white_pawn
    document.body.appendChild(pawn);
    for (i = 0; i < 8; i++) {
        ctx.drawImage(pawn, 30, 30);
    }


}
function Sample() {
    const [show, setShow] = useState(false);
    const handleClose = () => setShow(false);
    const handleShow = () => setShow(true);
    function startTask(){
    var c = document.getElementById("chessboard");
    }
    function renderTooltip(props) {
        return (
          <Tooltip id="button-tooltip" {...props}>
            Github
          </Tooltip>
        );
      }
  return (
    <Container>
        <CardDeck>
            <Card style={{overflow:'none'}}>
                <Card.Img variant="top" src={board_im}/>
                <Card.Body>
                    <Card.Title>Chess Board Game</Card.Title>
                    <Card.Text>
                        This is an implementation of chess using Python for all the logic to illustrate how far simple ideas can go.
                    </Card.Text>
                    </Card.Body>
                <Card.Footer style={{verticalAlign:'middle'}}>
                    <Button variant='primary' onClick={handleShow}>Play Game</Button>
                    <OverlayTrigger overlay={<Tooltip id="tooltip-disabled">GitHub!</Tooltip>}>
				<span className="d-inline-block" style={{float:'right'}}>

				</span>
			</OverlayTrigger> 
                </Card.Footer>
            </Card>
            <Card>
                <Card.Img variant="top" src="holder.js/100px160" />
                <Card.Body>
                <Card.Title>Card title</Card.Title>
                <Card.Text>
                    This card has supporting text below as a natural lead-in to additional
                    content.{' '}
                </Card.Text>
                </Card.Body>
                <Card.Footer>
                <small className="text-muted">Last updated 3 mins ago</small>
                </Card.Footer>
            </Card>
            <Card>
                <Card.Img variant="top" src="holder.js/100px160" />
                <Card.Body>
                <Card.Title>Card title</Card.Title>
                <Card.Text>
                    This is a wider card with supporting text below as a natural lead-in to
                    additional content. This card has even longer content than the first to
                    show that equal height action.
                </Card.Text>
                </Card.Body>
                <Card.Footer>
                <small className="text-muted">Last updated 3 mins ago</small>
                </Card.Footer>
            </Card>
        </CardDeck>
        <Modal id='test' show={show} onHide={handleClose} onEntering={draw} onEntered={startTask}>
        <Modal.Header closeButton>
          <Modal.Title>Chess</Modal.Title>
        </Modal.Header>
        <Modal.Body id="game_loc" onLoad={()=>console.log('Trying to see')} style={{textAlign: 'center'}}>
            </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleClose}>
            Close
          </Button>
          <Button variant="primary" onClick={handleClose}>
            Save Changes
          </Button>
        </Modal.Footer>
      </Modal> 
    </Container>


  );
}

export default Sample;
