import React, {useState, useEffect, Component} from 'react';
import logo from './logo.svg';
import './App.css';
import Jumbotron from 'react-bootstrap/Jumbotron';
import Toast from 'react-bootstrap/Toast';
import Container from 'react-bootstrap/Container';
import Navbar from 'react-bootstrap/Navbar';
import { Button, NavDropdown, Nav, Card } from 'react-bootstrap';
import 'bootstrap/dist/css/bootstrap.css';
import Board from './board.js'
import Scrollspy from 'react-scrollspy'

const squareStyles = {
    display:'flex',
    width:'100%',
    height:'100%',
}
const BOARD_SIZE = 560
function draw(){
    var c = document.getElementById("ChessBoard");
    var ctx = c.getContext("2d");
    var startX= 0, startY = 0;
    var blockSide = 560 / 8;
    var count = 0;
    for (var i = 0; i <= 8; i++){
        for (var j = 0; j <= 8; j++){
            ctx.moveTo(0, blockSide * j); 
            ctx.lineTo(BOARD_SIZE, blockSide * j);
            ctx.stroke(); // Draws vertical bars

            ctx.moveTo(blockSide * i, 0);
            ctx.lineTo(blockSide * i, BOARD_SIZE);
            ctx.stroke(); // Draws horizontal bars

            }
        }

    ctx.fillRect(startX, startY, blockSide, blockSide);

    for (var i = 0; i < 8; i += 1){
        for (var j = 0; j < 8; j += 1){
            startX = blockSide * i;
            startY = blockSide * j;
            if (count % 2 == 0){
                ctx.fillStyle = "#b5651d";
                console.log(count);
            } else {
                ctx.fillStyle = "#915117";
                console.log(count);
            }
            ctx.fillRect(startX, startY, blockSide, blockSide);
            //count = (count % 2 == 0) ? 1 : 0;
            count += 1;

        }
    }
}
class Square extends React.Component{
    render() {
        return (
            <div style={squareStyles, {backgroundColor:this.props.color}}>
                Hello can you hear me
            </div>
        );
    }
}
function Sample() {


  return (
    <Container>
        <Jumbotron id="home">
            <canvas id='ChessBoard' width={BOARD_SIZE} height={BOARD_SIZE} style={{solid : '#d3d3d3'}} onClick={e => draw()}>Your browser does not support the graphics.</canvas>
        </Jumbotron>

    </Container>


  );
}

export default Sample;
